mapper.select:
SELECT balance_id, account_id, currency, available_amount, version_number, created_at, updated_at 
FROM balances WHERE account_id = #{accountId}
ORDER BY currency

SELECT balance_id, account_id, currency, available_amount, version_number, created_at, updated_at 
FROM balances 
WHERE account_id = #{accountId} AND currency = #{currency}
